import { useState } from "react";
import Expenses from "./component/expense/Expenses";
import NewExpence from "./component/newExpence/NewExpence";
let dummy = [
  { id: 1, date: new Date(2021,3,28), Title: "school fee", Amount: 200 },
  { id: 1, date: new Date(2021,3,28), Title: "school fee", Amount: 200 },
];
function App() {
  const [expenses, setExpenses] = useState(dummy);
  fetch("http://localhost:3000/Expense").then(Response=>{
    Response.json
  })
  const addExpenceHandler = (expense) => {
    const updatedExpense = [expense, ...expenses];
    setExpenses(updatedExpense);
    // console.log(expense);
    console.log(expenses);
  };
  return (
    <div className="App">
      <NewExpence onAddExpence={addExpenceHandler} />
      <Expenses item={expenses} />
    </div>
  );
}

export default App;
